pragma solidity >=0.4.25 <0.6.0;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "./VoteToken.sol";

contract Voting {

    using SafeMath for uint256;

/* EVENTS */
  event voteCasted(address voter, uint pollID, bytes vote, uint weight);
  event pollCreated(address creator, uint pollID, uint quorumPercentage, string description, uint votingLength);
  event pollStatusUpdate(bool status);

  /* Determine the current state of a poll */
  enum PollStatus { IN_PROGRESS, TALLY, PASSED, REJECTED }

 /* POLL */
  struct Poll {
    address creator;
    PollStatus status;
    uint quorumPercentage;
    uint yeaVotes;
    uint nayVotes;
    string description;
    address[] voters;
    uint expirationTime;
    mapping(address => Voter) voterInfo;
  }

  /* VOTER */
  struct Voter {
    bool hasVoted;
    bytes vote;
    uint weight;
  }

/* TOKEN MANAGER */
  struct TokenManager {
    uint tokenBalance;
    mapping(uint => uint) lockedTokens;
    uint[] participatedPolls;
  }

  /* STATE VARIABLES */
  mapping(uint => Poll) public polls;
  mapping(address => TokenManager) public bank;
  uint public pollCount;
  VoteToken public token;

/* CONSTRUCTOR */
  constructor(address _vote_token_address) public {
    require(_vote_token_address != address(0), "Please provide VoteTokenAddress");
    token = VoteToken(_vote_token_address);
  }

}
